mapper.select:
select count(id)
from user
where id = #{id}

select id, password, name, email, date_format(joindate, '%y.%m.%d') joindate
from user
where id = #{id}

mapper.update:
update user
password = #{password}, email = #{email},
where id = #{id}

mapper.select:
select name, id, email
from user
where id = #{id} and password = #{password}

select name, id, email, joindate
from user
where id = #{id}
